mapper.insert:
INSERT INTO BALLONDOR.WORK_LIST
(
    USER_ID    
,   WORK   
,	REGI_DATE     
) 	
	SELECT #{ user_id }
	     , #{ work }
	     , NOW() 
	  FROM DUAL	
	 WHERE NOT EXISTS (
						  SELECT 1
						    FROM BALLONDOR.WORK_LIST
						   WHERE WORK    = #{ work }
						     AND USER_ID = #{ user_id }
					  )
